DROP TABLE IF EXISTS `xdict`;
CREATE TABLE `xdict` (
  `id` decimal(17,3) NOT NULL DEFAULT '0.000' COMMENT '编码',
  `idp` decimal(17,3) NOT NULL DEFAULT '0.000' COMMENT '上级编码',
  `_lock` tinyint(4) NOT NULL DEFAULT '0',
  `cat` char(16) NOT NULL DEFAULT '' COMMENT '数据归属，保留',
  `code` char(20) NOT NULL DEFAULT '' COMMENT '代码，简称',
  `text` char(40) NOT NULL DEFAULT '' COMMENT '名称',
  `typ` char(16) NOT NULL DEFAULT '' COMMENT '分类（-1：系统保留，不可编辑；0：用户所有，可编辑）',
  `stat` char(16) NOT NULL DEFAULT '' COMMENT '状态（1：启用；other：停用）',
  `dtnew` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最新修改时间',
  `dtcreate` datetime DEFAULT NULL COMMENT '创建时间',
  `opcreate` int(11) NOT NULL DEFAULT '0' COMMENT '创建人',
  `ord` int(11) NOT NULL DEFAULT '0' COMMENT '排序号',
  `children` int(11) NOT NULL DEFAULT '0' COMMENT '子项计数',
  `expanded` tinyint(4) NOT NULL DEFAULT '0' COMMENT '是否展开（0：否；1：是）',
  `dtyp` char(16) NOT NULL DEFAULT '' COMMENT '值的类型（s：字符串；i：整数；f：浮点数；b：布尔值；d/dt：时间；a：自增长）',
  `dval` char(80) NOT NULL DEFAULT '' COMMENT '值',
  `dinfo` char(40) NOT NULL DEFAULT '' COMMENT '备注',
  PRIMARY KEY (`id`),
  UNIQUE KEY `xdict1` (`code`) USING BTREE,
  UNIQUE KEY `xdict2` (`idp`,`dval`) USING BTREE,
  KEY `xdict3` (`dtnew`) USING BTREE,
  KEY `xdict4` (`idp`,`ord`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT;

